import React, { useState } from "react";

const PocketItemContainer = (historys) => {
  const item = historys.historys;
  //   const [deleteItem, setDeleteitem] = useState(true);

  const deleteHandler = (id) => {
    alert("삭제");
    fetch(`http://localhost:3001/historys/${id}`, {
      method: "DELETE",
    });
    console.log("delete" + id);
  };
  return (
    <div>
      {item.map((history) => {
        return (
          <div key={history.id} className="itemBox">
            <div>{history.title}</div>
            <div>{history.amount}</div>
            <div>{history.createdAt}</div>
            <div>
              <button
                onClick={() => {
                  deleteHandler(history.id);
                }}
              >
                삭제
              </button>
              <button>수정</button>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PocketItemContainer;
