import "./App.css";
import React, { useEffect, useState } from "react";
import NewItemContainer from "./component/NewItemContainer";
import PocketItemContainer from "./component/PocketItemContainer";

function App() {
  const [historys, setHistorys] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setTimeout(() => {
      fetch("http://localhost:3001/historys")
        .then((res) => {
          if (!res.ok) {
            throw Error("could not fetch the data for that resource");
          }
          return res.json();
        })
        .then((data) => {
          setIsPending(false);
          setHistorys(data);
          setError(null);
        })
        .catch((err) => {
          setIsPending(false);
          setError(err.message);
        });
    }, 1000);
  }, []);
  return (
    <div className="App">
      {historys}
      <h1>my consumtion record</h1>
      <NewItemContainer />
      <PocketItemContainer historys={historys} />
      {/* <div>{historys[0].title}</div> */}
    </div>
  );
}

export default App;
